import React, { useState, useContext } from 'react';
import CompetitionContext from '../../contexts/competitions/competitionContext';

const ParticipantItem = ({participant: {_id, alias, firstName}, isAdmin}) => {
  const [deleteToggle, setDeleteToggle] = useState(false);

  const { kickUserFromCompetition } = useContext(CompetitionContext);

  return (
    <React.Fragment>
      <span className='leaderboard-row'>{alias ? alias : firstName}</span>
      {isAdmin && 
        <button onClick={() => setDeleteToggle(true)}>
          <i className='fas fa-times'/>
        </button>
      }
      {deleteToggle &&
        <div className="form-group">
          <span>Are you sure you want to kick this user from this competition?</span>
          <input
            type='button'
            value='No, keep my account.'
            className='btn btn-primary btn-block'
            onClick={() => setDeleteToggle(false)}
          />
          <input
            type='button'
            value='Yes, delete my account.'
            className='btn btn-danger btn-block'
            onClick={() => kickUserFromCompetition(_id)}
          />
        </div>
      }
    </React.Fragment>
  )
}

export default ParticipantItem;
