import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import GoalContext from '../../contexts/goals/goalContext';
import GoalItem from './GoalItem';

const Goals = () => {
  const goalContext = useContext(GoalContext);
  const { getUserGoals, userGoals, loading } = goalContext;

  useEffect(() => {
    getUserGoals();
    //eslint-disable-next-line
  }, []);

  let currentListItems = null;
  let pastListItems = null;

  if (loading)
    currentListItems, pastListItems = (
      <li className='collection-item'>
        <h6>Loading...</h6>
      </li>
    )
  else if (userGoals !== null && userGoals.length === 0 && !loading) {
    currentListItems = (
      <li className='collection-item center'>
        You have no saved goals. Add one!
      </li>
    );
  }
  else {
    currentListItems = (
      <React.Fragment>
        {userGoals && userGoals.map(goal => <GoalItem goal={goal} key={goal._id} />)}
      </React.Fragment>
    );
    pastListItems = (
      <React.Fragment>
        {userGoals && userGoals.map(goal => <GoalItem goal={goal} key={goal._id} />)}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      {/*Current Goals*/}
      <ul className='collection'>
        <li className='collection-header'>
          <h1 className='collection-header'>Current Goals</h1>
        </li>
        {currentListItems}
        <li className='collection-footer'>
          <Link to='/goalform' className='text-secondary'>
            <p><i className='fas fa-plus'/> Add Goal</p>
          </Link>
        </li>
      </ul>
      {/*Past Goals*/}
      <ul className='collection'>
        <li className="collection-header">
          <h1 className="collection-header">Past Goals</h1>
        </li>
        {pastListItems}
      </ul>

    </React.Fragment>
  );
};

export default Goals;
