import React from 'react'

const LeaderboardItem = ({goal: {tracker}, type, time, name}) => {

  //calc progress
  let count = 0;
  let progress = '';
  if (type === 'pass/fail'){
    for (let i = 0; i < tracker.length; i++) {
      if (tracker[i]) count++;
    }
    progress = `${count} / ${(time > tracker.length) ? tracker.length : time + 1}`
  }
  else if (type === 'total') {
    for (let i = 0; i < tracker.length; i++) {
      count += tracker[i];
    }
    progress = `${count}`
  }
  else {
    let temp = tracker.filter(value => value !== null)
    count = temp.pop() - tracker[0];
    progress = `${count > 0 ? '+' : ''}${count}`
  }

  return (
    <div className='leaderboard-row'>
      <span>{name}</span>
      <span>{progress}</span>
    </div>
  )
}

export default LeaderboardItem;
