import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import GoalList from './GoalList';

const GoalTable = ({goals, isPast, isGoal}) => {
  const [isOpen, setIsOpen] = useState(true);

  const status = isPast ? 'Past' : 'Current';
  const type = isGoal ? 'Goal' : 'Competition';

  return (
    <React.Fragment>
      {(!isPast || goals.length !== 0) &&
        <ul className='collection'>
          <li className='collection-header header-with-btn'>
            <h2>{status} {type}s</h2>
            <button 
              className='btn btn-primary right'
              onClick={() => setIsOpen(!isOpen)}
            >
              <i className={isOpen ? 'fas fa-minus' : 'fas fa-plus'}/>
            </button>
          </li>
          {isOpen &&
            <React.Fragment>
              <GoalList goals={goals} isOwner={true} isGoal={isGoal}/>
              {!isPast &&
                <li className='collection-footer'>
                  <Link to='/goalform' className='text-secondary'>
                    <p className='margin-025'>
                      <i className='fas fa-plus'/> Add {type}
                    </p>
                  </Link>
                </li>
              }
            </React.Fragment>
          }
        </ul>
      }
    </React.Fragment>
  )
}

export default GoalTable;
