import React, { useContext, useState, useEffect } from 'react';

import AlertContext from '../../contexts/alerts/alertContext';
import { Link } from 'react-router-dom';
// import GoalPassFail from '../goals/GoalPassFail';
// import GoalTotal from '../goals/GoalTotal';
// import GoalDifference from '../goals/GoalDifference';

const CompetitionTable = () => {

  const { 
    goalCurrent, 
    clearCurrentGoal, 
  } = useContext(GoalContext);

  const { 
    competition, 
    competitionGoals, 
    competitionParticipants, 
    competitionError, 
    deleteCompetition, 
    clearCompetition 
  } = useContext(CompetitionContext);

  const { 
    setAlert, 
    clearAlert 
  } = useContext(AlertContext);

  const [deleteToggle, setDeleteToggle] = useState(false);

  const { name, startDate, duration } = goalCurrent;


  //handleDelete
  const handleDelete = async () => {
    await deleteCompetition(_id);
    if (competitionError) {
      setAlert(competitionError);
    }
    else {
      setAlert('Competition deleted!', true);
      props.history.push('/');
      clearCurrentGoal();
      clearCompetition();
    }
  };

  // //handleSave - passed down to goal containers
  // const handleSave = async (record) => {
  //   await updateGoalTracker(record, _id);
  //   if (goalsError) {
  //     setAlert(goalsError);
  //   }
  //   else {
  //     setAlert('Goal saved!');
  //   }
  // }

  return (
    <div className='form-container'>
      {!Object.entries(competition).length ? (
        <h2>Loading...</h2>
      ) : (
        <React.Fragment>
          <h2 className='collection-header'>{name}</h2>
          {/* Goal Container */}
          {/* Modify Button */}
          {time <= duration &&
            <React.Fragment>
              <Link 
                to='/competitionform' 
                className='btn btn-primary btn-block center' 
                onClick={clearAlert}
              >
                Modify Competition
              </Link>
            </React.Fragment>
          }
          {/* Delete Button */}
          {!deleteToggle &&
          <React.Fragment>
            <p className='lr-border'/>
            <button 
              className='btn btn-primary btn-block' 
              onClick={() => setDeleteToggle(true)}
            >
              Delete Competition
            </button>
          </React.Fragment>
        }        
        {/*Delete module*/}
        {deleteToggle && (
          <React.Fragment>
            <hr/>
            <span className='alert lr-border'>
              Are you sure you want to delete this competition? This will affect multiple users, and deletions cannot be undone.
            </span>
            <button
              className='btn btn-danger btn-split'
              onClick={handleDelete}
            >Yes</button>
            <button
              className='btn btn-primary btn-split'
              onClick={() => setDeleteToggle(false)}
            >No</button>
          </React.Fragment>
        )}
        </React.Fragment>
      )}
    </div>
  )
}

export default CompetitionTable;
