import React, { useState, useContext } from 'react';
import GoalContext from '../../contexts/goals/goalContext';
import AlertContext from '../../contexts/alerts/alertContext';
//alert context

const GoalForm = () => {
  const [goal, setGoal] = useState({
    name: '',
    duration: 1,
    startDate: Date.now(),
    type: 'pass/fail',
    units: '',
    frequency: 1,
    total: 1,
    privacy: 'only me',
    assume: false
  });

  const {name, duration, startDate, type, units, frequency, total, privacy } = goal;

  const goalContext = useContext(GoalContext);
  const { addGoal } = goalContext;

  const alertContext = useContext(AlertContext);
  const { setAlert, clearAlerts } = alertContext;

  const handleSubmit = e => {
    e.preventDefault();
    clearAlerts();
    if(type === 'pass/fail')
      setGoal({ ...goal, total: 0 });
    else
      setGoal({ ...goal, frequency: 7});
    if(name && duration && startDate && type && frequency && total) {
      addGoal(goal);
      //await response
      //message 'Goal added'
      //reset state
      //redirect
      setGoal({
        name: '',
        duration: '',
        startDate: '',
        type: '',
        units: '',
        frequency: '',
        privacy: ''
      });
    }
    else 
      setAlert('Please enter all fields.');
  };

  const handleChange = e => {
    if (e.target.name === 'frequency' || e.target.name === 'duration' || e.target.name === 'total')
      setGoal({
        ...goal,
        [e.target.name]: parseInt(e.target.value)
      })
    else
      setGoal({
        ...goal, 
        [e.target.name]: e.target.value
      });
  };

  return (
    <div className='form-container'>
      <h1>Add Goal</h1>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Goal Name</label>
          <input 
            type='text' 
            name='name' 
            onChange={handleChange}
            value={name}
            autoFocus={true}
          />
        </div>
        <div className="form-group">
          <label>Goal Duration (days)</label>
          <input 
            type='number' 
            name='duration' 
            onChange={handleChange}
            value={duration}
          />
        </div>
        <div className="form-group">
          <label>What type of goal would you like?
            <select
              name='type'
              value={type}
              onChange={handleChange}>
              <option value='pass/fail'>Pass/Fail  (e.g. I went to the gym today)</option>
              <option value='total'>Total  (e.g. I ran three miles today)</option>
              <option value='difference'>Difference  (e.g. I lost three pounds this month)</option>
            </select>  
          </label>
        </div>
        <div className="form-group">
          {type === 'pass/fail' && (
            <React.Fragment>
              <label>How often do you want to hit your goal?</label>
              <select
                name='frequency'
                value={frequency}
                onChange={handleChange}>
                <option value='7'>Daily</option>
                <option value='6'>Six days a week</option>
                <option value='5'>Five days a week</option>
                <option value='4'>Four days a week</option>
                <option value='3'>Three days a week</option>
                <option value='2'>Twice a week</option>
                <option value='1'>Once a week</option>
                <option value='30'>Once a month</option>
              </select>  
            </React.Fragment>
          )}
          {type === 'total' && (
            <React.Fragment>
              <label>What total number do you want to hit?</label>
              <input 
                type='number' 
                name='total' 
                onChange={handleChange}
                value={total}
                min='0'
              />
            </React.Fragment>
          )}
          {type === 'difference' && (
            <React.Fragment>
              <label>What difference do you want to achieve?</label>
              <input 
                type='number' 
                name='total' 
                onChange={handleChange}
                value={total}
              />
            </React.Fragment>
          )}
        </div>
        <div className="form-group">
          <label>Who can see your goal?
            <select
              name='privacy'
              value={privacy}
              onChange={handleChange}>
              <option value='only me'>Only Me</option>
              <option value='friends'>My Friends</option>
              <option value='public'>Everyone</option>
            </select>  
          </label>
        </div>
        <div className="form-group">
          <label>Start Date</label>
          <input 
            type='date' 
            name='startDate' 
            onChange={handleChange}
            value={startDate}
          />
        </div>
        {type !== 'pass/fail' && (
          <div className="form-group">
            <label>Units (e.g lbs or miles)</label>
            <input 
              type='text' 
              name='units' 
              placeholder='Units (Example: miles)' 
              onChange={handleChange}
              value={units}
            />
          </div> 
        )}
        <input 
          type='submit' 
          value='Add Goal' 
          className='btn btn-block btn-primary'
        />
      </form>
    </div>
  )
}

export default GoalForm;
