import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import FriendContext from '../../contexts/friends/friendContext';
import FriendItem from './FriendItem';
import FriendSearchbar from './FriendSearchbar';
import FriendRequests from './FriendRequests';

const Friends = () => {
  const friendContext = useContext(FriendContext);
  const { getFriends, friends, friendsLoading } = friendContext;

  useEffect(() => {
    getFriends();
    //eslint-disable-next-line
  }, []);

  //populate list
  let listItems = '';
  if (friendsLoading) {
    listItems = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    );
  }
  else if (friends.length === 0) {
    listItems = (
      <li className='collection-item center collection-item-block'>
        You have no friends ... awkward :/
        <br/>
        It's okay, click below to find some!
      </li>
    );
  }
  else {
    listItems = (
      <React.Fragment>
        {friends.map(friend => <FriendItem friend={friend} key={friend._id} />)}
      </React.Fragment>
    )
  }

  return (
    <ul className='collection'>
      <li className='collection-header'>
        <h2>Friends!</h2>
      </li>
      <FriendSearchbar />
      <FriendRequests />
      <FriendList />
      {listItems}
      <li className='collection-footer'>
        <Link to='/search' className='text-secondary'>
          <p className='margin-025'><i className='fas fa-plus'/> Add Friend</p>
        </Link>
      </li>
    </ul>
  );
};

export default Friends;
