import {
  REGISTER_SUCCESS,
  REGISTER_FAIL,
  USER_LOADED,
  AUTH_ERROR,
  LOGIN_SUCCESS,
  LOGIN_FAIL,
  LOGOUT,
  SET_AUTH_LOADING,
  CLEAR_AUTH_ERRORS
} from '../types';

// token: localStorage.getItem('token'),
// isAuthenticated: null,
// user: null,
// error: null,
// loading: false

export default (state, action) => {
  switch(action.type) {
    case USER_LOADED:
      return {
        ...state,
        isAuthenticated: true,
        user: action.payload,
        loading: false
      }
    case LOGIN_SUCCESS:
    case REGISTER_SUCCESS:
      return {
        ...state,
        token: action.payload,
        isAuthenticated: true,
        error: null,
        loading: false
      }
    case LOGIN_FAIL:
    case REGISTER_FAIL:
    case AUTH_ERROR:
    case LOGOUT:
      localStorage.removeItem('token');
      return {
        ...state,
        token: null,
        isAuthenticated: false,
        user: null,
        error: action.payload,
        loading: false
      }
    case SET_AUTH_LOADING: 
      return {
        ...state,
        loading: true
      }
    case CLEAR_AUTH_ERRORS:
      return {
        ...state,
        error: null
      }
    default: 
      return state
  };
};
