import React, { useContext, useState, useEffect } from 'react';
import GoalContext from '../../../contexts/goals/goalContext';
import CompetitionContext from '../../../contexts/competitions/competitionContext';
import GoalProgress from '../../goals/page/GoalProgress';
import GoalInfo from '../../goals/page/GoalInfo';
import CompChart from './CompChart';
import AlertContext from '../../../contexts/alerts/alertContext';
import { useHistory } from 'react-router-dom';
import CompButtons from './CompButtons';

const CompetitionTable = ({time, isAdmin, isAdminView, isStarted}) => {

  const { 
    goalCurrent, 
    updateGoalTracker,
    goalsError
  } = useContext(GoalContext);

  const { competition } = useContext(CompetitionContext);

  const { setAlert } = useContext(AlertContext);

  const { name, units, tracker, type, _id } = goalCurrent;

  const [record, setRecord] = useState(tracker);

  //fill missed past values in tracker array
  useEffect(() => {
    if (type === 'pass/fail') {
      setRecord(record.map((value, index) => {
        if (index < time && value === null) {
          value = false;
        }
        return value;
      }));
    }
    //eslint-disable-next-line
  }, []);

  //calc goal value of user
  let value = 0;
  if (type === 'difference') {
    let temp = record.filter(value => value !== null)
    value = temp.pop() - record[0];
  } else if (type === 'total') {
    for (let i = 0; i < record.length; i++ ) {
      value += record[i];
    }
  } else if (type === 'pass/fail') {
    for (let i = 0; i < record.length; i++ ) {
      if (record[i] === true) 
        value++;
    }
  }

  //handleSave - passed down to goal containers
  const handleSave = async (record) => {
    await updateGoalTracker(record, _id);
    if (goalsError) {
      setAlert(goalsError);
    }
    else {
      setAlert('Goal saved!');
    }
  }

  let isOwner = true;

  return (
    <div className='competition-table-container'>
      {!Object.entries(competition).length ? (
        <h2>Loading...</h2>
      ) : (
        <React.Fragment>
          <h2 className='collection-header'>{name}</h2>
          {isStarted && 
            <CompChart 
              goal={goalCurrent}
              record={record}
              setRecord={setRecord} 
              time={time}
              isComplete={isComplete}
              isOwner={isOwner}
              isStarted={isStarted}
            />
          }
          {isStarted && !isComplete &&
            <GoalProgress 
              type={type}
              time={time}
              record={record}
              setRecord={setRecord}
              units={units}
            />
          }
          <GoalInfo 
            goal={goalCurrent}
            record={record}
            time={time}
            value={value}
            isStarted={isStarted}
          />
          <CompButtons 
            isAdmin={isAdmin}
            isAdminView={isAdminView}
            isOwner={isOwner}
            isStarted={isStarted}
            isActive={isActive}
            handleSave={handleSave}
            record={record}
          />        
        </React.Fragment>
      )}
    </div>
  )
}

export default CompetitionTable;
