import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import GoalContext from '../../contexts/goals/goalContext';

const GoalItem = ({goal: { _id, name, startDate, duration }}) => {
  const goalContext = useContext(GoalContext);
  const { deleteGoal } = goalContext;

  const handleDelete = () => {
    deleteGoal(_id);
  }

  return (
    <li className='collection-item'>
      <div className='flex'>
        <h3 className='vertical-center'><Link to={`/goal/${_id}`}>{name}</Link></h3>
      </div>
      <div>
        <span className='right'>Day: {Date.now()-startDate}/{duration}</span>
        <br/>
        <span className='right'>100%</span>
      </div>
    </li>
  )
};

GoalItem.propTypes = {
  goal: PropTypes.object.isRequired
}

export default GoalItem;
