import React from 'react'

const LeaderboardItem = ({goal, type, time, name}) => {
  let score = 0;
  if (type === 'pass/fail') {
    score = 1;
  }
  else if (type === 'total') {
    score = 2;
  }
  else {
    score = 3;
  }

  return (
    <div className='space-between'>
      <span>{name}</span>
      <span>{score}</span>
    </div>
  )
}

export default LeaderboardItem;
