import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import userimg from '../../resources/userimg.png';
import PropTypes from 'prop-types';
import FriendContext from '../../contexts/friends/friendContext';

const SearchItem = ({user: {firstName, lastName, email, _id}}) => {
  const friendContext = useContext(FriendContext);
  const { friends } = friendContext;

  const avatar = null;

  const handleClick = () => {
    //set current
  }

  // //handle if friend or not
  // const button = '';
  // if (_id) {

  // }

  const buttonClass = null;

  return (
    <li className='search-item'>
      <Link className='search-link' onClick={handleClick} to={`/friend/${_id}`}>
        <div className="flex">
          <img
            src={avatar !== null ? avatar : userimg}
            alt={firstName.charAt(0)}
          />
        </div>
        <div className='search-container'>
          <span className='search-name'>{firstName} {lastName}</span>
          <br/>
          <span className='search-email'>{email}</span>
        </div>
        <button
          className={`search-btn ${buttonClass}`}
          onClick={handleClick}
        >
          Button!
        </button>
      </Link>
    </li>
  );
};

SearchItem.propTypes = {
  user: PropTypes.object.isRequired,
};

export default SearchItem;
