import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import userimg from '../../resources/userimg.png';
import PropTypes from 'prop-types';
import RequestContext from '../../contexts/requests/requestContext';
import FriendContext from '../../contexts/friends/friendContext';
import AlertContext from '../../contexts/alerts/alertContext';

const SearchItem = ({status, user: {firstName, lastName, email, _id}}) => {

  const requestContext = useContext(RequestContext);
  const { addRequest, deleteRequest } = requestContext;

  const friendContext = useContext(FriendContext);
  const { addFriend, setCurrentFriend } = friendContext;

  const alertContext = useContext(AlertContext);
  const { setAlert } = alertContext;

  //configure button(s) based on status
  let buttonMsg = 'Send Friend Request';
  let buttonClass = 'btn-caution';

  if (status === 'friend') {
    buttonMsg = 'Your Friend';
    buttonClass = 'btn-success';
  } else if (status === 'received') {
    buttonMsg = 'Accept Request';
    buttonClass = 'btn-success';
  } else if (status === 'sent') {
    buttonMsg = 'Request Pending...';
    buttonClass = 'btn-blue';
  }

  const handleClick = e => {
    if (status === 'stranger') {
      addRequest(_id);
    } else if (status === 'friend') {
      //go to page
      console.log('redirect to friend')
    } else if (status === 'received' && e.target.name === 'btn1') {
      //accept request
      console.log('accept request')
    } else if (status === 'received' && e.target.name === 'btn2') {
      //reject request
      console.log('reject request')
    } else if (status === 'sent') {
      //delete request
      console.log('delete request')
    }
  }

  const avatar = null;

  return (
    <li className='search-item'>
      <Link className='search-link' onClick={handleClick} to={`/friend/${_id}`}>
        <div className="flex">
          <img
            src={avatar !== null ? avatar : userimg}
            alt={firstName.charAt(0)}
          />
        </div>
        <div className='search-container'>
          <span className='search-name'>{firstName} {lastName}</span>
          <br/>
          <span className='search-email'>{email}</span>
        </div>
        <button
          className={`search-btn ${buttonClass}`}
          onClick={handleClick}
          name='btn1'
        >
          {buttonMsg}
        </button>
        {status === 'received' &&
        <button
          className='search-btn btn-danger'
          onClick={handleClick}
          name='btn2'
        >
          Delete Request
        </button>
        }
      </Link>
    </li>
  );
};

SearchItem.propTypes = {
  user: PropTypes.object.isRequired,
  status: PropTypes.string.isRequired
};

export default SearchItem;
