import React from 'react';
import { Link } from 'react-router-dom';
import userimg from '../../resources/userimg.png';
import PropTypes from 'prop-types';

const SearchItem = ({friendIds, user: {firstName, lastName, email, _id}}) => {
  const avatar = null;

  //configure button based on friend and request status
  let buttonClass = 'btn-caution';
  let buttonMsg = 'Add Friend';

  if (friendIds.includes(_id)) {
    buttonClass = 'btn-success';
    buttonMsg = 'Friend'
  }

  const handleClick = () => {
    //if friend, go to friend page
    //else if not friend, send request
    //else if friend request is pending, delete request
    //else if they requested you accept/reject request
  }

  return (
    <li className='search-item'>
      <Link className='search-link' onClick={handleClick} to={`/friend/${_id}`}>
        <div className="flex">
          <img
            src={avatar !== null ? avatar : userimg}
            alt={firstName.charAt(0)}
          />
        </div>
        <div className='search-container'>
          <span className='search-name'>{firstName} {lastName}</span>
          <br/>
          <span className='search-email'>{email}</span>
        </div>
        <button
          className={`search-btn ${buttonClass}`}
          onClick={handleClick}
        >
          {buttonMsg}
        </button>
      </Link>
    </li>
  );
};

SearchItem.propTypes = {
  user: PropTypes.object.isRequired,
  friendIds: PropTypes.array.isRequired
};

export default SearchItem;
