import React, { useState, useContext } from 'react';
import CompetitionContext from '../../contexts/competitions/competitionContext';

const ParticipantItem = ({participant: {_id, alias, firstName}, isAdmin, compId}) => {
  const [deleteToggle, setDeleteToggle] = useState(false);

  const { kickUserFromCompetition } = useContext(CompetitionContext);

  return (
    <React.Fragment>
      <div className={`table-info space-between lr-border ${deleteToggle && 'btn-caution'}`}>
        <span >{alias ? alias : firstName}</span>
        {isAdmin && 
          <button className='btn-participants btn-primary' onClick={() => setDeleteToggle(true)}>
            <i className='fas fa-times'/>
          </button>
        }
      </div>
      <div>
        {deleteToggle &&
          <div className="lr-border">
            <span className='btn-caution'>Are you sure you want to kick this user from this competition?</span>
            <input
              type='button'
              value='No'
              className='btn btn-primary btn-block'
              onClick={() => setDeleteToggle(false)}
              style={{margin: 0}}
            />
            <input
              type='button'
              value='Yes'
              className='btn btn-danger btn-block'
              onClick={() => kickUserFromCompetition(compId, _id)}
            />
          </div>
        }
      </div>
    </React.Fragment>
  )
}

export default ParticipantItem;
