import React, { useContext, useState } from 'react';
import FriendContext from '../../../contexts/friends/friendContext';
import FriendFriendList from './FriendFriendList';

const FriendFriends = () => {
  const friendContext = useContext(FriendContext);
  const { friendCurrent } = friendContext;

  const [isFriendsOpen, setIsFriendsOpen] = useState(true);

  return (
    <ul className='collection'>
      <li className='collection-header header-with-btn'>
        <h2>{friendCurrent.firstName}'s Friends!</h2>
        <button 
          className='btn btn-primary right'
          onClick={() => setIsFriendsOpen(!isFriendsOpen)}
        >
          <i className={isFriendsOpen ? 'fas fa-minus' : 'fas fa-plus'}/>
        </button>
      </li>
      {isFriendsOpen &&
        <React.Fragment>
          <FriendFriendList />
        </React.Fragment>
      }
    </ul>
  );
};

export default FriendFriends;
