import React from 'react'
import LeaderboardItem from './LeaderboardItem'

const Leaderboard = ({goals, participants, type, isMax}) => {

  let leaderboardList = '';

  if(participants.length === 0 || goals.length === 0) {
    leaderboardList = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    )
  }
  else {
    let scoredGoals = goals.map(goal => {
      //calc progress
      const {tracker} = goal;
      let count = 0;
      if (type === 'pass/fail'){
        for (let i = 0; i < tracker.length; i++) {
          if (tracker[i]) count++;
        }
      }
      else if (type === 'total') {
        for (let i = 0; i < tracker.length; i++) {
          count += tracker[i];
        }
      }
      else {
        let temp = tracker.filter(value => value !== null)
        count = temp.pop() - tracker[0];
      }
      let temp = participants.find(user => user._id === goal.user);
      let name = temp.alias ? temp.alias : temp.firstName;
      return {name, count, _id: goal._id}
    })

    console.log(scoredGoals)

    if (isMax)
      scoredGoals.sort((a, b) => a.count - b.count);
    else 
      scoredGoals.sort((a, b) => b.count - a.count);

    console.log(scoredGoals)

    leaderboardList = (
      scoredGoals.map(goal => {
        return <LeaderboardItem name={goal.name} count={goal.count} type={type} key={goal._id} />
      })
    );
  }

  return (
    <div className='competition-container'>
      <ul className='collection'>
        <li className='collection-header'>
          <h3>Leaderboard</h3>
        </li>
        {leaderboardList}
        <p className='lrb-border'/>
      </ul>
    </div>
  );
};

export default Leaderboard;
