import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import moment from 'moment';
import GoalContext from '../../contexts/goals/goalContext';

const GoalItem = ({goal: { _id, name, startDate, duration, tracker, type, units }, setStatus}) => {
  const goalContext = useContext(GoalContext);
  const { setCurrent } = goalContext;

  let time = moment.utc().startOf('day').diff(startDate, 'days') + 1;
  if (time < 0)
    time = 0;
  else if (time > duration) {
    setStatus(true, false);
    return null;
  }

  checkCurrent(false, true);

  //calc progress
  let progress = '';
  if (type === 'pass/fail'){
    let count = 0;
    for (let i = 0; i < tracker.length; i++) {
      if (tracker[i]) count++;
    }
    progress = `Success: ${count} / ${time}`
  }
  else if (type === 'total') {
    let count = 0;
    for (let i = 0; i < tracker.length; i++) {
      count += tracker[i];
    }
    progress = `Total: ${count} ${units}`
  }
  else {
    let temp = tracker.filter(value => value !== null)
    let count = temp.pop() - tracker[0];
    progress = `Change: ${count} ${units}`
  }

  const handleClick = () => {
    setCurrent(_id);
  };

  return (
    <li className='collection-item'>
      <div className='flex'>
        <h3 className='vertical-center'>
          <Link onClick={handleClick} to={`/goal/${_id}`}>{name}</Link>
        </h3>
      </div>
      <div className='hide-sm'>
        {time > 0 ? (
        <React.Fragment>
          <span className='right'>Day: {time} / {duration}</span>
          <br/>
          <span className='right'>{progress}</span>
        </React.Fragment>
        ) : (
        <React.Fragment>
          <span className='right'>Begins </span>
          <br/>
          <span className='right'>{moment.utc(startDate).format('MMM Do')}</span>
        </React.Fragment>
        )}
      </div>
    </li>
  );
};

GoalItem.propTypes = {
  goal: PropTypes.object.isRequired
}

export default GoalItem;
