import React, { useState } from 'react';
import moment from 'moment';
import GoalChartTotal from './GoalChartTotal';
import PropTypes from 'prop-types';
import GoalProgress from './goalPieces/GoalProgress';
import GoalInfo from './goalPieces/GoalInfo';

const GoalTotal = ({handleSave, isOwner, goal, time, isStarted}) => {

  const { duration, startDate, units, total, tracker, type } = goal;

  const [record, setRecord] = useState(tracker);

  //calc progress so far
  let value = 0;
  for (let i = 0; i < record.length; i++ ) {
    value += record[i];
  }

  return (
    <React.Fragment>
      {isStarted ? (
        <React.Fragment>
          <ul>
            <GoalChartTotal 
              units={units} 
              record={record} 
              time={time}
            />
            {time !== duration && isOwner && (
              <GoalProgress 
                type={type}
                time={time}
                record={record}
                setRecord={setRecord}
                units={units}
              />
            )}
            <GoalInfo 
              goal={goal}
              record={record}
              time={time}
              value={value}
            />
          </ul>
          {/* SaveButton */}
          { time !== duration && isOwner &&
            <React.Fragment>
              <button 
                className='btn btn-primary btn-block' 
                onClick={() => handleSave(record)}
              >
                Save Goal
              </button>
              <p className='lr-border'/>
            </React.Fragment>
          }
        </React.Fragment>
      ) : (
        <ul>
          {/* Info */}
          <li className='table-info lr-border'>
            <div className='space-between'>
              <span>
                Goal: {total} {units}
              </span>
              <span className='right'>
                Begins {moment.utc(startDate).format('MMM Do')}
              </span>
            </div>
          </li>
          <li className='table-info lr-border'>
            <div className='space-between'>
              <span>
                Duration: {duration} days
              </span>
            </div>
          </li>
        </ul>
      )
      }
    </React.Fragment>
  )
}

GoalTotal.propTypes = {
  handleSave: PropTypes.func,
  goal: PropTypes.object.isRequired,
  isOwner: PropTypes.bool.isRequired
}

export default GoalTotal;
