import React, { useContext, useRef, useEffect } from 'react';
import SearchableUsersContext from '../../contexts/searchableUsers/searchableUsersContext';

const SearchPage = () => {
  const searchableUsersContext = useContext(SearchableUsersContext);
  const { getSearchableUsers, filterSearchableUsers, clearSearchableUsersFilter} = searchableUsersContext;

  const text = useRef('');

  useEffect(() => {
    getSearchableUsers();
    //eslint-disable-next-line
  }, []);

  const handleChange = e => {
    if (text.current.value)
      filterSearchableUsers(e.target.value);
    else clearSearchableUsersFilter();
  }

  return (
    <form>
      <input 
        type='text'
        placeholder='Search Users'
        onChange={handleChange}
      />
    </form>
  )
}

export default SearchPage;
