import {
  SET_ALERT,
  REMOVE_ALERT,
  CLEAR_ALERT
} from '../types';

export default (state, action) => {
  switch(action.type) {
    case SET_ALERT:
      return action.payload;
    case REMOVE_ALERT:
      if (state.id === action.payload)
        return state = {};
      else return state;
    case CLEAR_ALERT:
      if (!state.persist)
        return state = {};
      else return state;
    default: 
      return state;
  };
};
