import React from 'react'
import LeaderboardItem from './LeaderboardItem'

const Leaderboard = ({goals, time, participants}) => {

  let leaderboardList = '';

  console.log(participants);

  if(participants.length === 0 || goals.length === 0) {
    leaderboardList = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    )
  }
  else {
    leaderboardList = (
      goals.map(goal => {
        console.log(goal)
        let temp = participants.find(user => user._id === goal.user);
        let name = temp.alias ? temp.alias : temp.firstName;
        return <LeaderboardItem goal={goal} key={goal._id} time={time} type={goal.type} name={name}/>
      })
    );
  }


  return (
    <div className='leaderboard-container'>
      <ul className='collection'>
        <li className='collection-header'>
          <h3>Leaderboard</h3>
        </li>
        {leaderboardList}
        <p className='lrb-border'/>
      </ul>
    </div>
  );
};

export default Leaderboard;
