import React, { useState } from 'react';
import moment from 'moment';

const GoalPassFail = ({current: { name, duration, startDate, type, total, privacy, compId, tracker, completed, }}) => {
  const [record, setRecord] = useState(tracker);

  let time = moment().startOf('day').diff(startDate, 'days') + 1;

  const handleClick = e => {
    if (record[e.target.name] === null)
      setRecord(...record, record[e.target.name] = true);
    else if (record[e.target.name] === true)
      setRecord(...record, record[e.target.name] = false);
    else if (record[e.target.name] === false)
      setRecord(...record, record[e.target.name] = true);
    console.log(record);
  };

  const table = () => {
    let list = [];
    for(let i = 1; i <= duration / 7; i++) {
      list.push(
        <span className='table-row' key={i}>
          <h3 className='table-label'>Week {i}</h3>
          <span className='table-buttons'>
            {buttons(i-1)}
          </span>
        </span>);
    }
    return list;
  };

  const buttons = week => {
    let list = [];
    for(let i = 0; i < total; i++) {
      let loc = week * total + i;
      list.push(
        <button 
          className='table-btn'
          onClick={handleClick} 
          key={loc} 
          name={loc} 
        >
          {record[loc] && <i className='fas fa-check success-color'/>}
          {!record[loc] && record[loc] !== null && <i className='fas fa-times danger-color'/>}
          {record[loc] === null && ' '}
        </button>
      )
    }
    return list;
  };

  return (
    <ul>
      <li>
        {table()}
      </li>
      <li>Start Date: </li>
      <li>Total: XX / {duration / 7 * total} (XX%)</li>
    </ul>
  );
};

export default GoalPassFail;
