import React from 'react';
import Chart from 'react-google-charts';

const GoalChart = ({ duration, units, total, record, time }) => {
  const dataPoints = [['x', 'miles', 'total'], [0, 0, 0]];
  let runningTotal = 0;
  for (let i = 1; i <= time; i++) {
    runningTotal += record[i]
    dataPoints.push(
      [i, record[i], runningTotal]
    );
  }

  return (
  <div className='border'>
    <Chart
      chartType="LineChart"
      loader={<div>Loading Chart</div>}
      data={dataPoints}
      options={{
        hAxis: {
          title: 'Day',
          format: '#'
        },
        legend: {
          position: 'none'
        },
        vAxis: {
          title: 'Miles',
        },
      }}
      rootProps={{ 'data-testid': '1' }}
    />
  </div>
  );
}

export default GoalChart;
