import React, { useContext, useState, useEffect } from 'react';
import SearchableUsersContext from '../../contexts/searchableUsers/searchableUsersContext';
import SearchResults from './SearchResults';

const SearchPage = () => {
  const searchableUsersContext = useContext(SearchableUsersContext);
  const { getSearchableUsers, filterSearchableUsers, clearSearchableUsersFilter } = searchableUsersContext;

  const [text, setText] = useState('');

  useEffect(() => {
    getSearchableUsers();
    //eslint-disable-next-line
  }, []);

  const handleChange = async e => {
    setText(e.target.value)
    if (text === '')
      await clearSearchableUsersFilter();
    else
      await filterSearchableUsers(text);
  };

  return (
    <React.Fragment>
      <form>
        <input 
          type='text'
          value={text}
          placeholder='Search Users'
          onChange={handleChange}
        />
      </form>
      <ul className='collection'>
        <li className='collection-header'>
          <h2>Current Goals</h2>
        </li>
      </ul>
      <SearchResults/>
    </React.Fragment>
  );
};

export default SearchPage;
