import React, { useContext, useState, useEffect } from 'react';
import GoalContext from '../../contexts/goals/goalContext';
import CompetitionContext from '../../contexts/competitions/competitionContext';
import AuthContext from '../../contexts/auth/authContext';
import Leaderboard from './Leaderboard';
import Participants from './Participants';
import InviteTable from './InviteTable';
import moment from 'moment';
import CompetitionTable from './CompetitionTable';

const CompetitionPage = () => {
  const { goalCurrent: {startDate, duration} } = useContext(GoalContext);

  const { 
    getCompetitionGoals, 
    getCompetitionParticipants, 
    competition, 
    competitionGoals, 
    competitionParticipants
  } = useContext(CompetitionContext);

  const { user } = useContext(AuthContext);

  const [isAdmin, setIsAdmin] = useState(false);
  const [isAdminView, setIsAdminView] = useState(false);
  const [isStarted, setIsStarted] = useState(false);

  const { _id, adminIds } = competition;

  //get competition goals and participants
  useEffect(() => {
    if(Object.entries(competition).length !== 0) {
      getCompetitionGoals(_id);
      getCompetitionParticipants(_id);
      if(adminIds.includes(user._id))
        setIsAdmin(true);
    }
    //eslint-disable-next-line
  }, [competition])

  //calc time to determine which day of competition we are on
  let time = moment().startOf('day').diff(startDate, 'days');
  let timeHours = moment().startOf('day').diff(startDate, 'hours');
  if (time > duration)
    time = duration;

  if (timeHours >= 0 && !isStarted)
    setIsStarted(true);

  return (
    <div className='grid-1-2'>
      <div>
        {isAdmin && 
          <div className='collection'>
            <button className='btn btn-primary' onClick={() => setIsAdminView(!isAdminView)}>
              {isAdminView ? 'View as User' : 'View as Admin'}
            </button>
          </div>
        }
        {isStarted &&
          <Leaderboard 
            goals={competitionGoals} 
            time={time}
            participants={competitionParticipants}  
          />
        }
        {(isAdminView || !isStarted) && 
          <Participants 
            participants={competitionParticipants} 
            isAdmin={isAdmin}
            compId={_id}
          />
        }
        {isAdminView && !isStarted &&
          <InviteTable 
            participants={competitionParticipants}
          />
        }
      </div>
      <div>
        <CompetitionTable 
          time={time}
          isStarted={isStarted}
        />
      </div>
    </div>
  )
}

export default CompetitionPage;
