import React, { useState, useContext, useEffect } from 'react';
import moment from 'moment';
//import AlertContext from '../../contexts/alerts/alertContext';
import GoalChart from './GoalChart';

const GoalTotal = ({handleSave, current: { duration, startDate, units, total, compId, tracker, completed }}) => {
  //const alertContext = useContext(AlertContext);
  //const {setAlert, clearAlerts} = alertContext;

  const [record, setRecord] = useState(tracker);

  let runningTotal = 0;

  for (let i = 0; i < record.length; i++ ) {
    runningTotal += record[i];
  }

  useEffect(() => {
    setRecord(record.map(value => {
      if (value === null) {
        value = 0;
      }
      return value;
    }));
    //eslint-disable-next-line
  }, []);

  let time = moment().startOf('day').diff(startDate, 'days') + 1;
  if (time > duration)
    time = duration;

  console.log(time);
  console.log(units);

    //https://canvasjs.com/react-charts/line-chart/

  return (
    <React.Fragment>
      <ul>
        <GoalChart className='border' record={record} units={units} time={time}/>
        <li className='table-info lr-border'>
          <div className='space-between'>
            <span>
              Total: {runningTotal} {units}
            </span>
            <span>
              Day: {time} / {duration}
            </span>
          </div>
        </li>
        <li className='table-info lr-border'>You are XX% finished with your goal.</li>
      </ul>
      <button className='btn btn-primary btn-block' onClick={() => handleSave(record)}>
        Save Goal
      </button>
    </React.Fragment>
  )
}

export default GoalTotal;
