  }

  return (
    <li className='collection-item'>
      <h6>
        <Link to={`/goal/${_id}`}>{name}</Link>
      </h6>
      <p>
        Start Date: {startDate}
      </p>
      <p>
        Duration: {duration} days
      </p>
      <button onClick={handleDelete} >Delete</button>
    </li>
  )
};

GoalItem.propTypes = {
  goal: PropTypes.object.isRequired
}

export default GoalItem;
