import React, { useState, useContext, useEffect } from 'react';
import moment from 'moment';
import AlertContext from '../../contexts/alerts/alertContext';

const GoalPassFail = ({handleSave, current: { duration, startDate, total, compId, tracker, completed }}) => {
  const alertContext = useContext(AlertContext);
  const {setAlert, clearAlerts} = alertContext;

  const [record, setRecord] = useState(tracker);

  let time = moment().startOf('day').diff(startDate, 'days');

  useEffect(() => {
    setRecord(record.map((value, index) => {
      if (index < time && value === null) {
        value = false;
      }
      return value;
    }));
  }, []);

  //event.target will get the icon and fail, but event.currentTarget will get the button every time
  const handleClick = e => {
    let clickLoc = parseInt(e.currentTarget.name);
    if (time < clickLoc) {
      setAlert("You can't record the future.");
      setTimeout(() => {
        clearAlerts();
      }, 2000);
    }
    else setRecord(record.map((value, index) => {
      if (index === clickLoc) {
        if (value === null || value === false) {
          value = true;
        }
        else value = false;
      }
      return value;
    }))
  };

  const table = () => {
    let list = [];
    for(let i = 1; i <= duration / 7; i++) {
      list.push(
        <span className='table-row' key={i}>
          <h3 className='table-label'>Week {i}<span className='hide-sm'> - {moment(startDate.substring(0, 10)).add(7*i, 'day').format('MMM Do')}</span></h3>
          <span className='table-buttons'>
            {buttons(i-1)}
          </span>
        </span>);
    }
    return list;
  };

  const buttons = week => {
    let list = [];
    for(let i = 0; i < total; i++) {
      let loc = week * total + i;
      list.push(
        <button 
          className={`table-btn ${time === loc && 'table-btn-today'}`}
          onClick={handleClick} 
          key={loc} 
          name={loc} 
        >
          {record[loc] && <i className='fas fa-check success-color' name={loc}/>}
          {!record[loc] && record[loc] !== null && <i className='fas fa-times danger-color' name={loc}/>}
          {record[loc] === null && ' '}
        </button>
      )
    }
    return list;
  };

  return (
    <React.Fragment>
      <ul>
        <li>
          {table()}
        </li>
        <li className='table-info lr-border'>Start Date: {moment(startDate).format('MMMM Do, YYYY')}</li>
        <li className='table-info lr-border'>Success To Date: XX / {time + 1} (XX%)</li>
        <li className='table-info lr-border'>Success Total: XX / {duration / 7 * total} (XX%)</li>
      </ul>
      <button className='btn btn-primary btn-block' onClick={() => handleSave(record)}>
        Save Goal
      </button>
    </React.Fragment>
  );
};

export default GoalPassFail;
