import React, { useContext, useState, useEffect } from 'react';
import SearchableUsersContext from '../../contexts/searchableUsers/searchableUsersContext';
import SearchResults from './SearchResults';

const SearchPage = () => {
  const searchableUsersContext = useContext(SearchableUsersContext);
  const { getSearchableUsers, filterSearchableUsers, clearSearchableUsersFilter } = searchableUsersContext;

  const [text, setText] = useState('');

  useEffect(() => {
    getSearchableUsers();
    //eslint-disable-next-line
  }, []);

  const handleChange = e => {
    setText(e.target.value)
    if (text === '')
      clearSearchableUsersFilter();
    else
      filterSearchableUsers(text);
  };

  return (
    <form>
      <input 
        type='text'
        value={text}
        placeholder='Search Users'
        onChange={handleChange}
      />
      <SearchResults/>
    </form>
  );
};

export default SearchPage;
