import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import moment from 'moment';
import GoalContext from '../../contexts/goals/goalContext';
import { round } from 'mathjs';

const GoalItem = ({goal: { _id, name, startDate, duration, tracker }}) => {
  const goalContext = useContext(GoalContext);
  const { setCurrent } = goalContext;

  let time = moment().startOf('day').diff(startDate, 'days') + 1;
  if (time < 0)
    time = 0;
  else if (time > duration)
    return null;

  //calc progress
  let progress = '';
  if (type === 'pass/fail'){
    let count = 0;
    for (let i = 0; i < tracker.length; i++) {
      if (tracker[i]) count++;
    }
    progress = `${count} / ${tracker.length}`
  }
  else if (type === 'total') {
    let count = 0;
    for (let i = 0; i < tracker.length; i++) {
      count += tracker[i];
    }
    progress = `${count} ${units}`
  }
  else {
    let temp = tracker.filter(value => value !== null)
    let count = temp.pop() - tracker[0];
    progress = `${count} ${units}`
  }

  const handleClick = () => {
    setCurrent(_id);
  };

  return (
    <li className='collection-item'>
      <div className='flex'>
        <h3 className='vertical-center'>
          <Link onClick={handleClick} to={`/goal/${_id}`}>{name}</Link>
        </h3>
      </div>
      <div>
        {time > 0 ? (
        <React.Fragment>
          <span className='right'>Day: {time} / {duration}</span>
          <br/>
          <span className='right'>{progress}</span>
        </React.Fragment>
        ) : (
        <React.Fragment>
          <span className='right'>Begins </span>
          <br/>
          <span className='right'>{moment(startDate.substring(0, 10)).format('MMM Do')}</span>
        </React.Fragment>
        )}
      </div>
    </li>
  );
};

GoalItem.propTypes = {
  goal: PropTypes.object.isRequired
}

export default GoalItem;
