import React, { useState, useContext } from 'react';
import GoalContext from '../../contexts/goals/goalContext';
//alert context

const GoalForm = () => {
  const [goal, setGoal] = useState({
    name: '',
    duration: '',
    startDate: Date.now(),
    type: 'pass/fail',
    units: '',
    frequency: '',
    privacy: 'only me',
    compId: '',
    assume: ''
  });
  const {name, duration, startDate, type, units, frequency, privacy, compId, assume } = goal;

  const goalContext = useContext(GoalContext);
  const { addGoal } = goalContext;

  const handleSubmit = e => {
    e.preventDefault();
    if(name && duration && startDate && type && frequency)
      addGoal(goal);
      setGoal({
        name: '',
        duration: '',
        startDate: '',
        type: '',
        units: '',
        frequency: '',
        privacy: '',
        compId: '',
        assume: ''
      });
    //else
      //show alert
  };

  const handleChange = e => {
    setGoal({
      ...goal, 
      [e.target.name]: e.target.value
    });
  };

  return (
    <div>
      <h1>Add Goal</h1>
      <form onSubmit={e => handleSubmit(e)}>
        <label>What type of goal would you like?
          <select
            value={type}
            onChange={handleChange}>
            <option value='pass/fail'>Yes / No</option>
            <option value='total'>Total</option>
            <option value='difference'>Difference</option>
          </select>  
        </label>


        <input 
          type='text' 
          name='name' 
          placeholder='Goal Name' 
          onChange={handleChange}
          value={name}
          autoFocus={true}
        />
        <input 
          type='number' 
          name='duration' 
          placeholder='Duration (days)' 
          onChange={handleChange}
          value={duration}
        />
        <input 
          type='date' 
          name='startDate' 
          placeholder='Start Date' 
          onChange={handleChange}
          value={startDate}
        />
        <input 
          type='text' 
          name='units' 
          placeholder='Units (Example: miles)' 
          onChange={handleChange}
          value={units}
        />
        <p>Who do you want to be able to see your goal?</p>
        <input 
          type='checkbox' 
          name='privacy' 
          placeholder='Units (Example: miles)' 
          onChange={handleChange}
          checked={privacy}
        /> 
        <input 
          type='submit' 
          value='Add Goal' 
        />
      </form>

Yes / No (Did I or did I not succeed today? e.g. Went to the gym)
Total (How many did I achieve? e.g. Ran three miles)
Difference (How much now compared to the beginning? e.g. Lost 15 lbs)
    </div>
  )
}

export default GoalForm;
