import React, { useContext, useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import GoalContext from '../../contexts/goals/goalContext';
import AlertContext from '../../contexts/alerts/alertContext';
import CompetitionContext from '../../contexts/competitions/competitionContext';
import Leaderboard from './Leaderboard';
import Participants from './Participants';
// import GoalPassFail from '../goals/GoalPassFail';
// import GoalTotal from '../goals/GoalTotal';
// import GoalDifference from '../goals/GoalDifference';
import moment from 'moment';

const CompetitionPage = props => {
  const goalContext = useContext(GoalContext);
  const { goalCurrent, clearCurrentGoal, goalsError, updateGoalTracker } = goalContext;

  const alertContext = useContext(AlertContext);
  const { setAlert, clearAlert } = alertContext;

  const competitionContext = useContext(CompetitionContext);
  const { getCompetitionGoals, getCompetitionParticipants, competition, competitionGoals, competitionParticipants, competitionLoading, competitionError, deleteCompetition, clearCompetition } = competitionContext;

  const [deleteToggle, setDeleteToggle] = useState(false);

  console.log(goalCurrent);

  const { name, type, startDate, duration, compId } = goalCurrent;
  const { _id, goalId, adminIds, isMax } = competition;

  //console.log(goalsError, updateGoalTracker, type, goalId, adminIds, isMax, competitionLoading);

  //get competition goals and participants
  useEffect(() => {
    if(Object.entries(competition).length !== 0) {
      getCompetitionGoals(competition._id);
      getCompetitionParticipants(competition._id);
    }
    //eslint-disable-next-line
  }, [competition])

  //calc time to determine which day of competition we are on
  let time = moment().startOf('day').diff(startDate, 'days');


  //handleDelete
  const handleDelete = async () => {
    await deleteCompetition(_id);
    if (competitionError) {
      setAlert(competitionError);
    }
    else {
      setAlert('Competition deleted!', true);
      props.history.push('/');
      clearCurrentGoal();
      clearCompetition();
    }
  };

  //handleSave - passed down to goal containers
  const handleSave = async (record) => {
    await updateGoalTracker(record, _id);
    if (goalsError) {
      setAlert(goalsError);
    }
    else {
      setAlert('Goal saved!');
    }
  }

  return (
    <div className='grid-2'>
      <Participants 
        participants={competitionParticipants} 
      />
      <Leaderboard 
        goals={competitionGoals} 
        time={time}
        participants={competitionParticipants}  
      />
      <div className='form-container'>
      {!Object.entries(competition).length ? (
        <h2>Loading...</h2>
      ) : (
        <React.Fragment>
          <h2 className='collection-header'>{name}</h2>
          {/* Goal Container */}
          {/* Modify Button */}
          {time <= duration &&
            <React.Fragment>
              <Link 
                to='/competitionform' 
                className='btn btn-primary btn-block center' 
                onClick={clearAlert}
              >
                Modify Competition
              </Link>
            </React.Fragment>
          }
          {/* Delete Button */}
          {!deleteToggle &&
          <React.Fragment>
            <p className='lr-border'/>
            <button 
              className='btn btn-primary btn-block' 
              onClick={() => setDeleteToggle(true)}
            >
              Delete Competition
            </button>
          </React.Fragment>
        }        
        {/*Delete module*/}
        {deleteToggle && (
          <React.Fragment>
            <hr/>
            <span className='alert lr-border'>
              Are you sure you want to delete this competition? This will affect multiple users, and deletions cannot be undone.
            </span>
            <button
              className='btn btn-danger btn-split'
              onClick={handleDelete}
            >Yes</button>
            <button
              className='btn btn-primary btn-split'
              onClick={() => setDeleteToggle(false)}
            >No</button>
          </React.Fragment>
        )}
        </React.Fragment>
      )}
    </div>
    </div>
  )
}

export default CompetitionPage;
