import React from 'react';
import moment from 'moment';

const GoalPassFail = ({current: { name, duration, startDate, type, total, privacy, compId, tracker, completed, }}) => {

  let time = moment().startOf('day').diff(startDate, 'days') + 1;

  const temp = [
    true, true, true, true,
    false, false, false, false,
    null, null, null, null,
    true, false, null, true
  ];

  const table = () => {
    let list = [];
    for(let i = 1; i <= duration / 7; i++) {
      list.push(
        <span key={i}>
          Week {i}
          {buttons(i-1)}
        </span>);
    }
    return list;
  }

  const buttons = x => {
    let list = [];
    for(let i = 1; i <= total; i++) {
      let loc = x * total + i;
      console.log(loc);
      list.push(
        <button 
          //onClick={} 
          key={loc} 
          name={loc} 
          value={true}
        >
          {temp[loc] && <i className='fas fa-check'/>}
          {!temp[loc] && <i className='fas fa-times'/>}
          {temp[loc] === null && ' '}
        </button>
      )
    }
    return list;
  }

  return (
    <ul>
      <li>
        {table()}
      </li>
      <li>Start Date: </li>
      <li>Total: XX / {duration / 7 * total} (XX%)</li>
    </ul>
  )
}

export default GoalPassFail;
