import React, { useState, useContext, useEffect } from 'react';
import GoalContext from '../../contexts/goals/goalContext';
import CompetitionContext from '../../contexts/competitions/competitionContext';
import AlertContext from '../../contexts/alerts/alertContext';
import moment from 'moment';
import { useHistory } from 'react-router-dom';

const CompetitionForm = () => {
  const goalContext = useContext(GoalContext);
  const { addGoal, goalCurrent, clearCurrentGoal } = goalContext;

  const alertContext = useContext(AlertContext);
  const { alert, setAlert, clearAlert } = alertContext;

  const competitionContext = useContext(CompetitionContext);
  const { addCompetition, updateCompetition } = competitionContext;

  const [goal, setGoal] = useState({
    name: '',
    duration: 28,
    startDate: moment().startOf('day').format('YYYY-MM-DD'),
    type: 'pass/fail',
    units: '',
    total: 7,
    isPrivate: false,
    compId: null,
    initialValue: 0,
    started: false
  });

  const [isMax, setIsMax] = useState(true);

  //control if current or not
  useEffect(() => {
    if (Object.entries(goalCurrent).length) {
      setGoal({ 
        ...goalCurrent, 
        startDate: moment.utc(goalCurrent.startDate).startOf('day'),
        initialValue: goalCurrent.tracker[0]
      });
      if (moment(goalCurrent.startDate).startOf('day') < moment.utc().startOf('day')) {
        setGoal({
          ...goalCurrent, 
          started: true,
          initialValue: goalCurrent.tracker[0] 
        })
        setAlert('This competition has already begun, so some attributes cannot be changed.', true)
      }
    }
    //clear nonpersistant alert
    return () => {
      clearAlert();
    }
    //eslint-disable-next-line
  }, [])

  const message = Object.entries(goalCurrent).length ? 'Modify Competition' : 'Add Competition';

  const { name, duration, startDate, type, units, total, isPrivate, initialValue, started } = goal;

  let time = moment.utc().startOf('day').diff(startDate, 'days');

  const handleSubmit = async (e) => {
    e.preventDefault();

    //verify start date not in past
    if(time > 0 && !started) {
      setAlert('Start date cannot be in the past.');
      return null;
    }

    //verify finish date is not in past (for goals that have started)
    if(duration - time < 0){
      setAlert('Finish date cannot be in the past.');
      return null;
    }

    //try to submit 
    if(name && duration && startDate && type && total && (units || type === 'pass/fail')) {
      //add/update goal and tell user
      if (message === 'Modify Competition') {
        await updateCompetition({goal, isMax});
        setAlert('Competition updated!', true);
        clearCurrentGoal();
      } else {
        await addGoal(goal);
        setAlert('Competition added!', true);
      }
    }
    else 
      setAlert('Please enter all fields.');
  };

  //redirect to new goal page
  let history = useHistory();

  useEffect(() => {
    if (alert.msg === 'Competition updated!' || alert.msg === 'Competition added!') {
      addCompetition(goalCurrent._id, isMax);
      history.push('/competition');
    }
  }, [alert])


  const handleChange = e => {
    if (e.target.name === 'duration' || e.target.name === 'total' || e.target.name === 'initialValue') {
      if (e.target.value === '') 
        setGoal({ ...goal, [e.target.name]: ''});
      else 
        setGoal({ ...goal, [e.target.name]: parseInt(e.target.value)});
    }
    else if (e.target.name === 'type' && e.target.value === 'pass/fail')
      setGoal({ ...goal, total: 7, type: 'pass/fail' });
    else
      setGoal({
        ...goal, 
        [e.target.name]: e.target.value
      });
  };

  const handleClick = e => {
    setGoal({ ...goal, isPrivate: !isPrivate });
  };

  return (
    <div className='form-container'>
      <h1>{message}</h1>
      <form onSubmit={handleSubmit} autoComplete='off'>
        {/* Name */}
        <div className="form-group">
          <label>Competition Name</label>
          <input 
            type='text' 
            name='name' 
            onChange={handleChange}
            value={name}
            autoFocus={true}
          />
        </div>
        {/* Start Date */}
        <div className="form-group">
          <label>Start Date</label>
          <input 
            disabled={started}
            type='date' 
            name='startDate' 
            onChange={handleChange}
            value={started ? moment.utc(startDate).format('YYYY-MM-DD') : moment(startDate).format('YYYY-MM-DD')}
          />
        </div>
        {/* Duration */}
        <div className="form-group">
          <label>Competition Duration (days)</label>
          <input 
            type='number' 
            name='duration' 
            onChange={handleChange}
            value={duration}
          />
          {type === 'pass/fail' && !(Number.isInteger(duration / 7)) && (duration !== '') &&
            <span className='block small-text'>*Competition duration will be adjusted to {duration - (duration % 7) + 7} days to use full weeks.</span>
          }
        </div>
        {/* Type */}
        <div className="form-group">
          <label>What type of competition would you like?
            <select
              disabled={started}
              name='type'
              value={type}
              onChange={handleChange}
            >
              <option value='pass/fail'>Pass/Fail  (e.g. Stretch every day)</option>
              <option value='total'>Total  (e.g. Run 100 miles)</option>
              <option value='difference'>Difference  (e.g. Gain 10 lbs)</option>
            </select>  
          </label>
        </div>
        {/* Total */}
        <div className="form-group">
          {type === 'pass/fail' && (
            <React.Fragment>
              <label>
                How many days a week do you want participants to hit?
              </label>
              <select
                disabled={started}
                name='total'
                value={total}
                onChange={handleChange}
              >
                <option value='7'>Every day</option>
                <option value='6'>Six days a week</option>
                <option value='5'>Five days a week</option>
                <option value='4'>Four days a week</option>
                <option value='3'>Three days a week</option>
                <option value='2'>Twice a week</option>
                <option value='1'>Once a week</option>
              </select>  
            </React.Fragment>
          )}
          {type === 'difference' && (
            <React.Fragment>
              <label>What is your (personal) start number?</label>
              <input 
                disabled={started}
                type='number' 
                name='initialValue' 
                onChange={handleChange}
                value={initialValue}
                min='0'
              />
            </React.Fragment>
          )}
        </div>
        {/* Units */}
        {type !== 'pass/fail' && (
          <div className="form-group">
            <label>Units (e.g lbs or miles)</label>
            <input 
              type='text' 
              name='units' 
              placeholder='Units (Example: miles)' 
              onChange={handleChange}
              value={units}
            />
          </div> 
        )}
        {/* isPrivate */}
        <div className="form-group">
          <label className='block'>
            Who can see this competition?
          </label>
          <label className='switch'>
            <input
              type='checkbox'
              checked={!isPrivate}
              onChange={handleClick}
            />
            <span className='slider round'/>
          </label>
          <span className='register-span'>
            {isPrivate ? 'Only participants can see this competition.' : 'Friends of participants can see this competition.'}
          </span>
        </div>
        {/* isMax */}
        <div className="form-group">
          <label className='block'>
            How is this competition decided?
          </label>
          <label className='switch'>
            <input
              type='checkbox'
              checked={isMax}
              onChange={() => setIsMax(!isMax)}
            />
            <span className='slider round'/>
          </label>
          <span className='register-span'>
            This competition is won by the user with the {isMax ? 'highest' : 'lowest'} cumulative value.
          </span>
        </div>
        {/* Submit */}
        <input 
          type='submit' 
          value={message} 
          className='btn btn-block btn-primary'
        />
      </form>
    </div>
  );
};

export default CompetitionForm;
