import React from 'react'
import LeaderboardItem from './LeaderboardItem'

const Leaderboard = ({competitionGoals, goalsLoading}) => {

  let leaderboardList = '';

  if(loading)
    leaderboardList = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    )
  else 
    leaderboardList = (
      competitionGoals.map(goal => {
        return <LeaderboardItem goal={goal} />
      })
    );

  return (
    <div>
      <h3>Leaderboard</h3>
      <ul>
        {leaderboardList}
      </ul>
    </div>
  );
};

export default Leaderboard;
