import {
  GET_FRIENDS,
  ADD_FRIEND,
  DELETE_FRIEND,
  SET_CURRENT_FRIEND,
  CLEAR_CURRENT_FRIEND,
  SET_FRIEND_LOADING,
  FRIENDS_ERROR,
  CLEAR_FRIEND_ERRORS,
  CLEAR_FRIENDS,
} from '../types';

// friends: [],
// current: {},
// error: null,
// loading: true

export default (state, action) => {
  switch(action.type) {
    case GET_FRIENDS:
      return {
        ...state,
        friends: action.payload,
        friendIds: null,
        loading: false
      }
    case ADD_FRIEND:
      return {
        ...state,
        friends: [...state.friends, action.payload],
        friendIds: [...state.friendIds, action.payload._id],
        loading: false
      }
    case DELETE_FRIEND:
      return {
        ...state,
        friends: state.friends.filter(friend => friend._id !== action.payload),
        friendIds: state.friendIds.filter(id => id !== action.payload),
        loading: false
      }
    case SET_CURRENT_FRIEND:
      return {
        ...state,
        current: state.friends.find(friend => friend._id === action.payload)
      }
    case CLEAR_CURRENT_FRIEND:
      return {
        ...state,
        current: {}
      }
    case SET_FRIEND_LOADING:
      return {
        ...state,
        loading: true
      }
    case FRIENDS_ERROR:
      return {
        ...state,
        error: action.payload,
        loading: false
      }
    case CLEAR_FRIEND_ERRORS:
      return {
        ...state,
        error: null
      }
    case CLEAR_FRIENDS:
      return {
        ...state,
        friends: [],
        friendIds: [],
        current: {},
        error: null,
        loading: true
      }
    default: 
      return state
  }
}
