import React from 'react';
import Chart from 'react-google-charts';

const GoalChart = ({ duration, units, total, record, time }) => {
  const dataPoints = [['x', 'miles', 'total'], [0, 0, 0]];
  let runningTotal = 0;
  for (let i = 0; i <= time; i++) {
    runningTotal += record[i]
    dataPoints.push(
      [i + 1, record[i], runningTotal]
    );
  }

  return (
  <div className='border'>
    <Chart
      chartType="LineChart"
      loader={<div>Loading Chart</div>}
      data={dataPoints}
      options={{
        chartArea: {
          left: 0,
          top: 0,
          width: '100%',
          height: '80%',
        },
        axisTitlesPosition: 'none',
        hAxis: {
          title: 'none',
          textPosition: 'none',
          baselineColor: 'none',
          viewWindow: {
            min: 0
          }
        },
        legend: {
          position: 'bottom'
        },
        vAxis: {
          title: 'Miles',
          textPosition: 'none',
          baselineColor: 'black',
          baselineWidth: '2px'
        },
        titlePosition: 'none'
      }}
      rootProps={{ 'data-testid': '1' }}
    />
  </div>
  );
}

export default GoalChart;
