import React from 'react';
import Chart from 'react-google-charts';

const CompChartDifference = ({competitionArray, units, time, duration}) => {

  //format data array and configure tooltip 
  let dataPointsZero = ['x'];
  let dataPointsOne = [0];
  let dataPointsBeyond = [];

  //the length of the values coming after
  //has fewer edge cases than using time
  let valuesLength = 0;
  if (competitionArray.length !== 0) {
    valuesLength = competitionArray[0][2].length;
  }

  //configure dataPoints [0] and[1]
  for (let i = 0; i < competitionArray.length; i++) {
    dataPointsZero.push(
      competitionArray[i][0],
      { role: 'tooltip', type: 'string', p: { html: true }}
    )
    dataPointsOne.push(
      0, 'Start'
    )
  }

  //configure rest of dataPoints
  for (let i = 1; i < valuesLength; i++) {
    let list = [i];
    for (let j = 0; j < competitionArray.length; j++) {
      let value = competitionArray[j][2][0] - competitionArray[j][2][i];
      list.push(
        value,
        `Day ${i} \n Change: ${value} \n Current: ${competitionArray[j][2][i]} ${units}`
      )
    }
    dataPointsBeyond.push(list);
  }

  let dataPoints = [dataPointsZero, dataPointsOne, ...dataPointsBeyond];

  //format data array and configure tooltip
  // const dataPoints = [
  //   [
  //     'x', 
  //     'Daily', 
  //     { role: 'tooltip', type: 'string', p: { html: true }},
  //   ],
  //   [
  //     0,
  //     record[0],
  //     `Start \n ${record[0]} ${units}`
  //   ]
  // ];

  const chartMax = (time === duration) ? duration : time + 1;

  // //set dataPoints - time + 1 because we are storing start value in [0]
  // for (let i = 1; i <= time + 1; i++) {
  //   if (record[i] !== null) {
  //     dataPoints.push([
  //       i, 
  //       record[i], 
  //       `Day ${i} \n ${record[i]} ${units}`,
  //     ]);
  //   }
  // }

  return (
    <div className='border'>
      <Chart
        chartType="LineChart"
        loader={<div>Loading Chart</div>}
        data={dataPoints}
        options={{
          chartArea: {
            left: 0,
            top: 0,
            width: '100%',
            height: '100%',
          },
          axisTitlesPosition: 'none',
          hAxis: {
            textPosition: 'none',
            baselineColor: 'none',
            gridlines: {
              color: 'transparent'
            },
            viewWindow: {
              min: 0
            },
            maxValue: chartMax
          },
          legend: {
            position: 'none'
          },
          vAxis: {
            textPosition: 'none',
            baselineColor: 'none',
          },
          titlePosition: 'none',
        }}
      />
    </div>
  )
}

export default CompChartDifference;
