import React, { useState, useContext } from 'react';
import CompetitionContext from '../../contexts/competitions/competitionContext';

const ParticipantItem = ({participant: {_id, firstName, lastName}, isAdmin, compId}) => {
  const [deleteToggle, setDeleteToggle] = useState(false);

  const { kickUserFromCompetition } = useContext(CompetitionContext);

  return (
    <React.Fragment>
      {deleteToggle && <hr/>}
      <div className='participant-row space-between lr-border'>
        <span className='block'>{firstName} {lastName}</span>
        {isAdmin && 
          <button 
            className='btn-participants btn-primary' 
            onClick={() => setDeleteToggle(!deleteToggle)}
          >
            <i className='fas fa-times'/>
          </button>
        }
      </div>
      {deleteToggle &&
        <div className="lr-border">
          <span className='participant-row btn-caution block'>Are you sure you want to kick this user from this competition?</span>
          <input
            type='button'
            value='No'
            className='btn btn-primary btn-split'
            onClick={() => setDeleteToggle(false)}
            style={{margin: 0}}
          />
          <input
            type='button'
            value='Yes'
            className='btn btn-danger btn-split'
            onClick={() => kickUserFromCompetition(compId, _id)}
            style={{margin: 0}}
          />
        </div>
      }
    </React.Fragment>
  )
}

export default ParticipantItem;
