import React from 'react'
import LeaderboardItem from './LeaderboardItem'

const Leaderboard = ({competitionGoals, loading}) => {

  let leaderboardList = '';

  if(loading) {
    console.log('loading', competitionGoals);
    leaderboardList = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    )
  }
  else {
    console.log('not loading', competitionGoals);
    leaderboardList = (
      competitionGoals.map(goal => {
        return <LeaderboardItem goal={goal} />
      })
    );
  }

  return (
    <div>
      <h3>Leaderboard</h3>
      <ul>
        {leaderboardList}
      </ul>
    </div>
  );
};

export default Leaderboard;
