import React, { useContext } from 'react';
import Chart from 'react-google-charts';
import CompetitionContext from '../../../contexts/competitions/competitionContext';

const CompChartTotal = ({time, units}) => {

  const { 
    competitionGoals, 
    competitionParticipants,
  } = useContext(CompetitionContext);

  let userGoals = competitionGoals.map(goal => {
    let user = competitionParticipants.find(user => user._id === goal.user);
    let name = user.alias ? user.alias : user.firstName;
    return {name, tracker: goal.tracker}
  })

  console.log('UserGoals: ', userGoals);
  //format data array and configure tooltip

  let dataPointsZero = ['x'];

  console.log('Goals: ', competitionGoals);
  console.log('Participants', competitionParticipants);

  //for (let i = 0, i <= competitionParticipants; )


  const dataPoints = [[
    'x', 
    'Daily', 
    { role: 'tooltip', type: 'string', p: { html: true }},
    'Total', 
    { role: 'tooltip', type: 'string', p: { html: true }}
  ], [
    0, 0, 'Start', 0, 'Start'
  ]];


  // //populate data array
  // let runningTotal = 0;
  // for (let i = 0; i <= time; i++) {
  //   runningTotal += record[i]
  //   dataPoints.push([
  //     i + 1, 
  //     record[i], 
  //     `Day ${i + 1} \n Daily: ${record[i]} ${units}`,
  //     runningTotal, 
  //     `Day ${i + 1} \n Total: ${runningTotal} ${units}`
  //   ]);
  // }

  return (
    <div className='border'>
      <Chart
        chartType="LineChart"
        loader={<div>Loading Chart</div>}
        data={dataPoints}
        options={{
          chartArea: {
            left: 0,
            top: 0,
            width: '100%',
            height: '100%',
          },
          axisTitlesPosition: 'none',
          hAxis: {
            textPosition: 'none',
            baselineColor: 'none',
            gridlines: {
              color: 'transparent'
            },
            viewWindow: {
              min: 0
            }
          },
          legend: {
            position: 'in'
          },
          vAxis: {
            textPosition: 'none',
            baselineColor: 'none',
          },
          titlePosition: 'none',
        }}
      />
    </div>
  )
}

export default CompChartTotal
