import React, { useContext, useState, useEffect } from 'react';
import AuthContext from '../../contexts/auth/authContext';
import AlertContext from '../../contexts/alerts/alertContext';
import GoalContext from '../../contexts/goals/goalContext';

const UserPage = props => {
  const authContext = useContext(AuthContext);
  const { user, updateUser, changeUserPassword, deleteUser, error, clearUserErrors } = authContext;

  const alertContext = useContext(AlertContext);
  const { setAlert, clearAlerts } = alertContext;

  const goalContext = useContext(GoalContext);
  const { deleteGoals } = goalContext;

  const [current, setCurrent] = useState({
    firstName: '',
    lastName: '',
    alias: '',
    searchable: true,
    email: '',
    oldPassword: '',
    newPassword: '',
    newPassword2: ''
  });
  const [passwordToggle, setPasswordToggle] = useState(false);
  const [editToggle, setEditToggle] = useState(false);
  const [deleteToggle, setDeleteToggle] = useState(false);

  const { firstName, lastName, alias, searchable, email, oldPassword, newPassword, newPassword2} = current;

  //populate current with user values
  useEffect(() => {
    setCurrent({...current, ...user});
    //eslint-disable-next-line
  },[user]);

  //set alert if error
  useEffect(() => {
    if(error === 'User updated!') {
      setEditToggle(false);
      setTimeout(() => {
        clearAlerts();
      }, 2000);
    }
    else if (error === 'Password changed!') {
      setPasswordToggle(false);
      setCurrent({...current, oldPassword: '', newPassword: '', newPassword2: ''});
      setTimeout(() => {
        clearAlerts();
      }, 2000);
    }
    else if (error === 'User deleted.') {
      setDeleteToggle(false);
      setTimeout(() => {
        clearAlerts();
      }, 2000);
    }
    if (error) {
      setAlert(error);
      clearUserErrors();
    }
    //eslint-disable-next-line
  }, [error]);

  //clear alerts before redirect
  useEffect(() => {
    return () => {
      clearAlerts();
    }
  }, []);

  const handlePassword = async () => {
    clearAlerts();
    if (newPassword !== newPassword2)
      setAlert('Passwords do not match.');
    else {
      await changeUserPassword({oldPassword, newPassword, newPassword2}, props.match.params.id);
    }
  };

  const handleEdit = async () => {
    clearAlerts();
    if (firstName === '') 
      setAlert('Please enter a first name.');
    else if (lastName === '')
      setAlert('Please enter a last name.');
    else if (email === '')
      setAlert('Please enter an email.');
    else {
      await updateUser(current);
    }
  };

  const handleDelete = async () => {
    clearAlerts();
    if (oldPassword === '')
      setAlert('Please enter your password.');
    else {
      await deleteUser(oldPassword, current._id);
      await deleteGoals(current._id);
    }
  }

  const handleChange = e => {
    setCurrent({...current, [e.target.name]: e.target.value});
  };

  const handleClick = () => {
    setCurrent({ ...current, searchable: !searchable });
  };

  return (
    <div className='form-container'>
      <h1>User Profile</h1>
      {/*Edit module*/}
      {editToggle && (
      <form autoComplete='off'>
        {/*First Name*/}
        <div className="form-group">
          <label htmlFor='firstName'>First Name</label>
          <input
            type='text'
            value={firstName}
            name='firstName'
            onChange={handleChange}
          />
        </div>
        {/*Last Name*/}
        <div className="form-group">
          <label htmlFor='lastName'>Last Name</label>
          <input
            type='text'
            value={lastName}
            name='lastName'
            onChange={handleChange}
          />
        </div>
        {/*Alias*/}
        <div className="form-group">
          <label htmlFor='alias'>Alias (optional - the name your friends will see)</label>
          <input
            type='text'
            value={alias}
            name='alias'
            onChange={handleChange}
          />
        </div>
        {/*Email Address*/}
        <div className="form-group">
          <label htmlFor='email'>Email Address</label>
          <input
            type='email'
            value={email}
            name='email'
            onChange={handleChange}
          />
        </div>
        {/*Privacy*/}
        <div className="form-group">
          <label className='block'>Privacy</label>
          <label className='switch'>
            <input
              type='checkbox'
              checked={searchable}
              name='searchable'
              onChange={handleClick}
            />
            <span className='slider round'/>
          </label>
          <span className='register-span'>Are other users allowed to search for your name, email, and alias? This is how friends will find you.</span>
        </div>
        {/*Save Button*/}
        <input 
          type='button'
          value='Save User'
          className='btn btn-primary btn-block'
          onClick={handleEdit}
        />
      </form>
      )}
      {/*Password module*/}
      {passwordToggle && (
      <form autoComplete='off'>
        {/*Old password*/}
        <div className="form-group">
          <label htmlFor='oldPassword'>Current Password</label>
          <input
            type='password'
            value={oldPassword}
            name='oldPassword'
            onChange={handleChange}
          />
        </div>
        {/*New Password*/}
        <div className="form-group">
          <label htmlFor='newPassword'>New Password</label>
          <input
            type='password'
            value={newPassword}
            name='newPassword'
            onChange={handleChange}
          />
        </div>
        {/*New Password 2*/}
        <div className="form-group">
          <label htmlFor='newPassword2'>Verify New Password</label>
          <input
            type='password'
            value={newPassword2}
            name='newPassword2'
            onChange={handleChange}
          />
        </div>
        {/*Save Button*/}
        <input
          type='button'
          value='Change Password'
          className='btn btn-primary btn-block'
          onClick={handlePassword}
        />
        {/*Cancel Button*/}
        <input
          type='button'
          value='Cancel'
          className='btn btn-primary btn-block'
          onClick={() => setPasswordToggle(false)}
        />
      </form>
      )}
      {/*Delete module*/}
      {deleteToggle && (
      <form autoComplete='off'>
        <span>Deleting your account cannot be undone and your information cannot be recovered. Any goals, competitions, or friendships you have will be permanently lost. Are you sure you want to delete your account?</span>
        <div className="form-group">
          <label htmlFor='oldPassword'>Password</label>
          <input
            type='password'
            value={oldPassword}
            name='oldPassword'
            onChange={handleChange}
          />
        </div>
        <input
            type='button'
            value='No, keep my account.'
            className='btn btn-primary btn-block'
            onClick={() => setDeleteToggle(false)}
          />
        <input
            type='button'
            value='Yes, delete my account.'
            className='btn btn-danger btn-block'
            onClick={handleDelete}
          />
      </form>
      )}
      {/*User Information, Edit, Change Password, and Delete Button*/}
      {!editToggle && !passwordToggle && !deleteToggle && (
        <React.Fragment>
          <p/><strong>Name: </strong>{firstName} {lastName}
          <p/><strong>Alias: </strong>{alias !== '' ? alias : 'none'}
          <p/><strong>Email: </strong>{email}
          <p/><strong>Privacy: </strong>Other users {searchable ? 'can' : 'cannot'} search for me using my name, email, and alias.
          <input
            type='button'
            value='Edit User'
            className='btn btn-primary btn-block'
            onClick={() => setEditToggle(true)}
          />
          <input
            type='button'
            value='Change Password'
            className='btn btn-primary btn-block'
            onClick={() => setPasswordToggle(true)}
          />
          <input
            type='button'
            value='Delete User'
            className='btn btn-danger btn-block'
            onClick={() => setDeleteToggle(true)}
          />
        </React.Fragment>
      )}
    </div>
  )
}

export default UserPage;
