import {
  SET_ALERT,
  REMOVE_ALERT,
  //CLEAR_ALERTS
} from '../types';

export default (state, action) => {
  switch(action.type) {
    case SET_ALERT:
      return action.payload;
    case REMOVE_ALERT:
      if (state.id === action.payload.id)
        return {};
      else return state;
    //case CLEAR_ALERTS:
      //return [];
    default: 
      return state;
  };
};
