import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import GoalContext from '../../contexts/goals/goalContext';
import GoalItem from './GoalItem';
import moment from 'moment';

const Goals = () => {
  const goalContext = useContext(GoalContext);
  const { getUserGoals, userGoals, loading } = goalContext;

  useEffect(() => {
    getUserGoals();
    //eslint-disable-next-line
  }, []);

  let currentGoals = null, pastGoals = null, currentList = null;

  if (loading) {
    currentList = (
      <li className='collection-item'>
        <h6>Loading...</h6>
      </li>
    )
  }
  else if (userGoals !== null && !loading) {
    pastGoals = userGoals.filter(goal => {
      let time = moment().startOf('day').diff(goal.startDate + goal.duration, 'days') + 1;
      console.log(time);
      if (time > 0)
        return true;
      else
        return false;
    })
    currentGoals = userGoals.filter(goal => {
      let time = moment().startOf('day').diff(goal.startDate + goal.duration, 'days') + 1;
      console.log(time);
      if (time > 0)
        return false;
      else
        return true;
    })
  }

  if (currentGoals.length === 0) {
    currentList = (
      <li className='collection-item center'>
        You have no saved goals. Add one!
      </li>
    );
  }
  else {
    listItems = (
      <React.Fragment>
        {currentGoals.map(goal => <GoalItem goal={goal} key={goal._id} />)}
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      {/*Current Goals*/}
      <ul className='collection'>
        <li className='collection-header'>
          <h1 className='collection-header'>Current Goals</h1>
        </li>
        {listItems}
        <li className='collection-footer'>
          <Link to='/goalform' className='text-secondary'>
            <p><i className='fas fa-plus'/> Add Goal</p>
          </Link>
        </li>
      </ul>
      {/*Past Goals*/}
      { pastGoals.length !== 0 && (
        <ul className='collection'>
          <li className="collection-header">
            <h1 className="collection-header">Past Goals</h1>
          </li>
          {pastGoals.map(goal => <GoalItem goal={goal} key={goal._id} />)}
        </ul>
      )}

    </React.Fragment>
  );
};

export default Goals;
