import React from 'react';
import Chart from 'react-google-charts';

const CompChartTotal = ({competitionArray, time, units}) => {

  //format data array and configure tooltip 
  let dataPointsZero = ['x'];
  let dataPointsOne = [0];
  let dataPointsBeyond = [];

  console.log(competitionArray);

  //the length of the values coming after 
  let valuesLength = 0;
  if (competitionArray.length !== 0)
    valuesLength = competitionArray[2].length;

  for (let i = 0; i < competitionArray.length; i++) {
    dataPointsZero.push(
      competitionArray[i][0],
      { role: 'tooltip', type: 'string', p: { html: true }}
    )
    dataPointsOne.push(
      0, 'Start'
    )
  }
  for (let i = 0; i < valuesLength; i++) {
    for (let j = 0; j < competitionArray.length; j++)
    dataPointsBeyond.push(
      i + 1, 
      competitionArray[j][2][i],
      `Day ${i + 1} \n Total: ${units}`
    )
  }

  let dataPoints = dataPointsZero.concat(dataPointsOne, dataPointsBeyond)

  console.log(dataPoints);

  // //populate data array
  // let runningTotal = 0;
  // for (let i = 0; i <= time; i++) {
  //   runningTotal += record[i]
  //   dataPoints.push([
  //     i + 1, 
  //     record[i], 
  //     `Day ${i + 1} \n Daily: ${record[i]} ${units}`,
  //     runningTotal, 
  //     `Day ${i + 1} \n Total: ${runningTotal} ${units}`
  //   ]);
  // }

  return (
    <div className='border'>
      <Chart
        chartType="LineChart"
        loader={<div>Loading Chart</div>}
        data={dataPoints}
        options={{
          chartArea: {
            left: 0,
            top: 0,
            width: '100%',
            height: '100%',
          },
          axisTitlesPosition: 'none',
          hAxis: {
            textPosition: 'none',
            baselineColor: 'none',
            gridlines: {
              color: 'transparent'
            },
            viewWindow: {
              min: 0
            }
          },
          legend: {
            position: 'in'
          },
          vAxis: {
            textPosition: 'none',
            baselineColor: 'none',
          },
          titlePosition: 'none',
        }}
      />
    </div>
  )
}

export default CompChartTotal
