import React from 'react';
import ParticipantItem from './ParticipantItem';

const Participants = ({participants, isAdmin}) => {

  let participantList = '';

  if(participants.length === 0) {

    participantList = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    )
  }
  else {
    participantList = (
      participants.map(participant => {
        return <ParticipantItem participant={participant} key={participant._id} isAdmin={isAdmin}/>
      })
    );
  }


  return (
    <div className='leaderboard-container'>
      <ul className='collection'>
        <li className='collection-header'>
          <h3>Participants</h3>
        </li>
        {participantList}
        <p className='lrb-border'/>
      </ul>
    </div>
  )
}

export default Participants;
