import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import GoalContext from '../../contexts/goals/goalContext';
import AlertContext from '../../contexts/alerts/alertContext';
import GoalPassFail from './GoalPassFail';
import GoalTotal from './GoalTotal';
import GoalDifference from './GoalDifference';

const GoalPage = props => {
  const goalContext = useContext(GoalContext);
  const { current, clearCurrent, error, deleteGoal } = goalContext;

  const alertContext = useContext(AlertContext);
  const { setAlert, clearAlerts } = alertContext;

  const [deleteToggle, setDeleteToggle] = useState(false);

  const { name, _id, type } = current;
  !Object.entries(current).length && props.history.push('/');

  //https://canvasjs.com/react-charts/line-chart/

  const handleDelete = async () => {
    await deleteGoal(_id);
    if (error) {
      setAlert(error);
    }
    else {
      setAlert('Goal deleted!');
      props.history.push('/');
      clearCurrent();
      setTimeout(() => {
        clearAlerts();
      }, 2000);
    }
  };

  return (
    <div className='form-container'>
    {!Object.entries(current).length ? (
      <h1>Loading...</h1>
    ) : (
      <React.Fragment>
        <h1 className='collection-header'>{name}</h1>
        {type === 'pass/fail' && <GoalPassFail current={current} />}
        {type === 'total' && <GoalTotal current={current} />}
        {type === 'difference' && <GoalDifference current={current} />}
        <button className='btn btn-primary btn-block'>
          Save Goal
        </button>
        <p/>
        <Link to='/goalform' className='btn btn-primary btn-block goal-page-link' onClick={clearAlerts}>
          Modify Goal
        </Link>
        {!deleteToggle &&
          <React.Fragment>
            <p/>
            <button 
              className='btn btn-primary btn-block' 
              onClick={() => setDeleteToggle(true)}
            >
              Delete Goal
            </button>
          </React.Fragment>
        }
        {/*Delete module*/}
        {deleteToggle && (
          <React.Fragment>
            <span className='alert'>
              Are you sure you want to delete this goal? Deletions cannot be undone.
            </span>
            <button
              className='btn btn-danger btn-split'
              onClick={handleDelete}
            >Yes</button>
            <button
              className='btn btn-primary btn-split'
              onClick={() => setDeleteToggle(false)}
            >No</button>
          </React.Fragment>
        )}
      </React.Fragment>
    )}
    </div>
  )
}

export default GoalPage;
