import React, { useContext } from 'react';
import GoalContext from '../../contexts/goals/goalContext';
import GoalPassFail from './GoalPassFail';
import GoalTotal from './GoalTotal';
import GoalDifference from './GoalDifference';
import moment from 'moment';

const GoalPage = props => {
  const { goalCurrent } = useContext(GoalContext);

  const { name, type, startDate, duration } = goalCurrent;

  !Object.entries(goalCurrent).length && props.history.push('/');

  let timeHours = moment().startOf('day').diff(startDate, 'hours');
  const isStarted = timeHours >= 0 ? true : false;
  let time = moment().startOf('day').diff(startDate, 'days');
  if (time > duration)
    time = duration;

  return (
    <div className='form-container'>
    {!Object.entries(goalCurrent).length ? (
      <h2>Loading...</h2>
    ) : (
      <React.Fragment>
        <h2 className='collection-header'>{name}</h2>
        {type === 'pass/fail' && 
          <GoalPassFail 
            goal={goalCurrent} 
            isOwner={true}
            time={time}
            isStarted={isStarted}
          />
        }
        {type === 'total' && 
          <GoalTotal 
            goal={goalCurrent} 
            isOwner={true}
            time={time}
            isStarted={isStarted}
          />
        }
        {type === 'difference' && 
          <GoalDifference 
            goal={goalCurrent} 
            isOwner={true}
            time={time}
            isStarted={isStarted}
          />
        }
      </React.Fragment>
    )}
    </div>
  )
}

export default GoalPage;
