import React from 'react'
import LeaderboardItem from './LeaderboardItem'

const Leaderboard = ({goals, loading, time}) => {

  let leaderboardList = '';

  if(loading) {

    leaderboardList = (
      <li className='collection-item center collection-item-block'>
        Loading...
      </li>
    )
  }
  else {
    leaderboardList = (
      goals.map(goal => {
        let temp = participants.filter(user => user._id === goal.user);
        let name = temp.alias ? temp.alias : temp.firstname;
        return <LeaderboardItem goal={goal} key={goal._id} time={time} type={goal.type} participant={name}/>
      })
    );
  }

  return (
    <div>
      <h3>Leaderboard</h3>
      <ul>
        {leaderboardList}
      </ul>
    </div>
  );
};

export default Leaderboard;
